import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import { auth } from "./firebase"; // auth Í∞ÄÏ†∏Ïò§Í∏∞
import Signup from "./components/Signup";
import Login from "./components/Login.js";
import SelectTeam from "./components/SelectTeam.js";
import Logout from "./components/Logout.js";

function App() {
  const [selectedTeam, setSelectedTeam] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      if (user) {
        setIsLoggedIn(true); //Î°úÍ∑∏Ïù∏ ÏÉÅÌÉúÎ°ú ÏÑ§Ï†ï
        const savedItem = localStorage.getItem("selectedTeam");
        if (savedItem) {
          setSelectedTeam(savedItem);
        }
      } else {
        setIsLoggedIn(false); // Î°úÍ∑∏ÏïÑÏõÉ ÏÉÅÌÉúÎ°ú ÏÑ§Ï†ï
        setSelectedTeam(null);
      }
    });
    return () => unsubscribe(); // firebase Î¶¨Ïä§ÎÑà Ìï¥Ï†ú
  }, []);

  return (
    <Router>
      <div className="App">
        {/* RoutesÎ°ú Í∞Å ÌéòÏù¥ÏßÄ Í≤ΩÎ°ú Ï†ïÏùò */}
        <Routes>
          <Route
            path="/"
            element={
              <Home isLoggedIn={isLoggedIn} selectedTeam={selectedTeam} />
            }
          />
          {/* Home Ïª¥Ìè¨ÎÑåÌä∏Ïóê isLoggedIn, selectedTeam Îç∞Ïù¥ÌÑ∞ Ï†ÑÎã¨ */}
          {/* Ìôà ÌôîÎ©¥ */}
          <Route path="/login" element={<Login />} /> {/* Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄ */}
          <Route path="/signup" element={<Signup />} /> {/* ÌöåÏõêÍ∞ÄÏûÖ ÌéòÏù¥ÏßÄ */}
          <Route path="/select-team" element={<SelectTeam />} />
          {/* Íµ¨Îã® ÏÑ†ÌÉù ÌéòÏù¥ÏßÄ */}
        </Routes>
      </div>
    </Router>
  );
}

// Ìôà ÌôîÎ©¥ Ïª¥Ìè¨ÎÑåÌä∏
function Home({ isLoggedIn, selectedTeam, onLogout }) {
  return (
    <div>
      <h2>ÏäπÎ¶¨ÏöîÏ†ïüßöüèª‚Äç‚ôÄÔ∏è</h2>
      {isLoggedIn ? (
        <>
          <p>ÎÇòÏùò ÏÇ¨ÎûëÌïòÎäî {selectedTeam}‚öæÔ∏èüíó</p>
          <Logout onLogout={onLogout} />
        </>
      ) : (
        <>
          <p>Î°úÍ∑∏Ïù∏ ÎòêÎäî ÌöåÏõêÍ∞ÄÏûÖÏùÑ ÏßÑÌñâÌï¥Ï£ºÏÑ∏Ïöî.</p>
          <nav>
            <Link to="/login">
              <button>Î°úÍ∑∏Ïù∏</button>
            </Link>
            <Link to="/signup">
              <button>ÌöåÏõêÍ∞ÄÏûÖ</button>
            </Link>
          </nav>
        </>
      )}
    </div>
  );
}

export default App;
