import React, { useState } from "react";

const teams = [
  "두산 베어스",
  "LG 트윈스",
  "한화 이글스",
  "삼성 라이온즈",
  "KIA 타이거즈",
  "롯데 자이언츠",
  "NC 다이노스",
  "키움 히어로즈",
  "SSG 랜더스",
  "KT 위즈",
];

function SelectTeam() {
  const [selectedTeam, setSelectedTeam] = useState(null);

  const handleSelectTeam = (team) => {
    setSelectedTeam(team);
  };

  return (
    <div>
      <p>어떤 팀을 응원하시나요?</p>
      {selectedTeam && <p>나의 팀 : {selectedTeam}</p>}
      <div
        className="grid-container"
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(5, 1fr)",
          gap: "10px",
          justifyContent: "center",
        }}
      >
        {teams.map(
          (
            team,
            index // teams 배열을 순회하며 각 팀 이름에 대해서 하나의 버튼을 생성한다.
          ) => (
            <button
              key={index}
              onClick={() => handleSelectTeam(team)}
              style={{
                backgroundColor: selectedTeam === team ? "black" : "lightgrey",
                color: selectedTeam === team ? "white" : "black",
                width: "120px",
                height: "50px",
                padding: "10px",
                margin: "5px",
                border: "none",
                borderRadius: "15px",
                cursor: "pointer",
              }}
            >
              {team}
            </button>
          )
        )}
      </div>
    </div>
  );
}

export default SelectTeam;
